import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import Muestra from "./muestra";
import Botones from "./boton";


const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [count, setCount] = useState(0)

  return (
    <div>
    <Muestra text={"good"} count={good} />
    <Muestra text={"neutral"} count={neutral} />
    <Muestra text={"bad"} count={bad} />
    <Botones setCount={setGood} count={good} />
    <Botones setCount={setNeutral} count={neutral} />
    <Botones setCount={setBad} count={bad} />


    </div>
    );
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
